import React from 'react';

import {TableRow} from '../table-row/table-row.component';
import {SearchBox} from '../search-box/search-box.component';

import './table.style.css';

class Table extends React.Component {
    constructor() {
      super();
      this.state = {
        list: [],
        searchField: ''
      };
    }

    componentDidMount(){
        fetch('https://api.kawalcorona.com/')
        .then(response => response.json())
        .then(lists => this.setState({ list: lists }))
    }

    handleChange = (e) => {
        this.setState({ searchField: e.target.value });
      }

    render() {
        const { list, searchField } = this.state;
        const newList = list.map(({attributes}) => (
          attributes
        ));
        const filteredList = newList.filter(
            newList => newList.OBJECTID
          );
        return(
        <div className='container'>
                <SearchBox placeholder="Search" handleChange={this.handleChange}/>
                <table className='table'>
                <thead>
                    <tr>
                    <td>Country Region</td>
                    <td>Confirmed</td>
                    <td>Deaths</td>
                    <td>Recovered</td>
                    <td>Active</td>
                    </tr>
                </thead>
                <tbody>
                    {
                      list
                      .filter(
                        function( data, index ){
                          console.log("Filter : "+ data)
                          return data.attributes
                        }
                      )
                      .map(function(data, index){
                        console.log(data)
                        return (<TableRow key={data.OBJECTID} attributes={data} />)
                      }.bind(this))
                    }
                    {/* { list.map(({attributes}) => (
                        <TableRow key={attributes.OBJECTID} attributes={attributes}/>
                    ))} */}
                </tbody>
            </table>
        </div>
    )
    }

}

export default Table;
