import React, { Component } from "react";

import { CardList } from "./components/card-list/card-list.component";
import { SearchBox } from "./components/search-box/search-box.component";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      list: [],
      searchField:''
    };
  }

  componentDidMount(){
    fetch('https://api.kawalcorona.com/')
    .then(response => response.json())
    .then(lists => this.setState({ list: lists }))
  }

  handleChange = (e) => {
    this.setState({ searchField: e.target.value })
  }

  render() {
    const { list, searchField } = this.state;
    // const filteredlists = list.filter(
    //   list => list.name.toLowerCase().includes(searchField.toLowerCase())
    // );
    return (
      <div className="App">
        <h1>lists Roledex </h1>
        <SearchBox
          placeholder='Search lists'
           handleChange={ this.handleChange }
        />
        { list.map(({attributes}) => (
          <div key={attributes.OBJECTID}>
            <h3>{attributes.Country_Region}</h3>
            <table>
              <tr>
                <td>Confirmed</td>
                <td>Deaths</td>
                <td>Recovered</td>
                <td>Active</td>
              </tr>
              <tr>
                <td>
                  {attributes.Confirmed}
                </td>
                <td>
                  {attributes.Deaths}
                </td>
                <td>
                  {attributes.Recovered}
                </td>
                <td>
                  {attributes.Active}
                </td>
              </tr>
            </table>
          </div>
        ))}
        {/* <CardList list={filteredlists} />  */}
      </div>
    );
  }
}

export default App;
